  .then(data => {
    const chart = new G2.Chart({
      container: 'container',
      forceFit: true,
      height: 500,
      padding: 40
    });
    chart.source(data);
    chart.tooltip({
      showTitle: null
    });
    chart.coord('polar', {
      innerRadius: 0.2
    });
    chart.legend(false);
    chart.axis('week', {
      grid: null,
      line: null,
      tickLine: null,
      label: null
    });
    chart.axis('time', {
      line: null,
      tickLine: null,
      grid: null,
      label: {
        offset: 3
      }
    });
    chart.polygon()
      .position('time*week')
      .color('value', '#BAE7FF-#1890FF-#0050B3')
      .tooltip('week*time*value')
      .style({
        stroke: '#fff',
        lineWidth: 1
      });

    const values = [ 'Mon.', 'Tue.', 'Wed.', 'Thu.', 'Fri.', 'Sat.', 'Sun.' ];
    values.map((val, idx) => {
      chart.guide().text({
        top: true,
        position: [ 0, idx ],
        content: val,
        style: {
          fill: '#fff',
          textAlign: 'center',
          shadowBlur: 2,
          shadowColor: 'rgba(0, 0, 0, .45)'
        }
      });

      return null;
    });
    chart.render();
  });

